export interface PokemonTrainer {
    name: string;
    numberOfPokeballs: number;
    pokemon: Array<Pokemon>;
    gymBadges: string[];

    attack(pokemon: Pokemon, attackName: string);

    runAway(): string;

    healPokemon(pokemon: Pokemon);
}

export class Pokemon {
    static StartingHP = 100;
    name: string;
    trainerName: string;
    private hp: number;
    strength: number;
    private attacks: Array<string>;

    constructor(name: string, trainerName: string, strength: number) {
        this.name = name;
        this.trainerName = trainerName;
        this.strength = strength;
    }

    get attacks(): Array<string> {
        return this.attacks;
    }

    set attacks(attacks: Array<string>) {
        this.attacks = attacks;
    }

    attack(attackName: string): string {
        return attackName;
    }

    decreaseHp(amount: number): void {
        this.hp -= amount;
    }

    heal(): void {
        this.hp = Pokemon.StartingHP;
    }
}